con:configuration.scriptText:
import groovy.json.JsonSlurper

log.info messageExchange.modelItem.testCase.getTestStepByName("happy").getPropertyValue("response");
def response = context.expand('${happy#Response}');
log.info response;
slurperresponse = new JsonSlurper().parseText(response)
log.info (slurperresponse.id);

messageExchange.modelItem.testCase.testSuite.setPropertyValue("productId", slurperresponse.id.toString())

import groovy.json.JsonSlurper

def response = context.expand('${add 2nd product#Response}');
log.info response;
slurperresponse = new JsonSlurper().parseText(response)
log.info (slurperresponse.id);

messageExchange.modelItem.testCase.testSuite.project.setPropertyValue("productId", slurperresponse.id.toString())

